let audio = new Audio();

async function loadChristianRadio() {
  const url =
    "https://api.radio-browser.info/json/stations/bytag/christian";

  const res = await fetch(url);
  const data = await res.json();

  // Filter Hindi & English manually
  const radios = data.filter(s =>
    s.language.toLowerCase().includes("hindi") ||
    s.language.toLowerCase().includes("english")
  );

  radios.forEach((s, i) => {
    console.log(`${i + 1}. ${s.name}`);
    console.log("Lang:", s.language);
    console.log("URL:", s.url);
    console.log("------");
  });

  if (radios.length > 0) {
    audio.src = radios[0].url;
    audio.play();
  }
}

loadChristianRadio();
